import React, { useState, useEffect } from 'react';
import { Card, ListGroup, Button, Modal,Table,ButtonGroup } from 'react-bootstrap';
import Sidebar from './SideBar';
import { Link } from 'react-router-dom';

export default function Users() {
    //const [users, setUsers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [userToBlock, setUserToBlock] = useState(null);

    const users = [
        {   id:"1",
            name:"Juan",
            rol:"Trainer"
        },
        {
            id:"2",
            name:"Domingo",
            rol:"Atleta"
        },
        {
            id:"3",
            name:"Perón",
            rol:"Trainer"
        },

    ]

    /*
    useEffect(() => {
        async function getUsers() {
          const response = await fetch('https://api.example.com/users');
          const data = await response.json();
          setUsers(data);
        }
        getUsers();
    }, []);*/


    function handleBlockUser(user) {
        setUserToBlock(user);
        setShowModal(true);
    }

    function handleConfirmBlockUser() {
        // hacer petición para bloquear usuario
        console.log(`Usuario ${userToBlock.name} bloqueado`);
        setShowModal(false);
    }

    function handleCancelBlockUser() {
        setUserToBlock(null);
        setShowModal(false);
    }  

    return (
      <div>
        <Sidebar title={"Users"} />
        <Card style = {{justifyContent:"center",margin: "auto",alignItems: 'center',width:"80%",alignSelf:"center" }}>
        <h1>Lista de Usuarios</h1>
            {users.map(user => (
                <>
                <Card key={user.id} style={{ width: '18rem' }}>
                <Card.Body style={{ borderRadius:20 }}>
                    <Card.Title>{user.name}</Card.Title>

                    <ButtonGroup>
                        <Button variant="success"  as={Link} to={`/users/${user.id}`}> Ver Perfil</Button>
                        <Button variant="danger" onClick={() => handleBlockUser(user)}>
                        Bloquear Usuario
                        </Button>
                    </ButtonGroup>   

                    <ListGroup variant="flush">
                    <ListGroup.Item>
                        <Link to={`/users/${user.id}`}>Ver Perfil</Link>
                    </ListGroup.Item>
                    <ListGroup.Item>
                        <Button variant="danger" onClick={() => handleBlockUser(user)}>
                        Bloquear Usuario
                        </Button>
                    </ListGroup.Item>
                    </ListGroup>
                </Card.Body>
                </Card>



                </>
            ))}

<Table striped bordered hover variant="dark">
      <thead>
        <tr>
          <th>Id</th>
          <th>Name</th>
          <th>Rol</th>
          <th>Ver Perfil</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>1</td>
          <td>Mark</td>
          <td>Otto</td>
          <Link to={`/users/${user.id}`}>Ver Perfil</Link>
        </tr>
        <tr>
          <td>2</td>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
        </tr>
        <tr>
          <td>3</td>
          <td colSpan={2}>Larry the Bird</td>
          <td>@twitter</td>
        </tr>
      </tbody>
    </Table>
            <Modal show={showModal} onHide={handleCancelBlockUser}>
                <Modal.Header closeButton>
                <Modal.Title>Confirmación de Bloqueo de Usuario</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                ¿Estás seguro que quieres bloquear al usuario {userToBlock && userToBlock.name}?
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={handleCancelBlockUser}>
                    Cancelar
                </Button>
                <Button variant="danger" onClick={handleConfirmBlockUser}>
                    Bloquear
                </Button>
                </Modal.Footer>
            </Modal>
        </Card>



      </div>
    );
  };


