import React, { useState, useEffect } from 'react';
import Sidebar from './SideBar';
import { Card, ListGroup, ListGroupItem, Badge, Button } from "react-bootstrap";
import { Link, useParams,useLocation } from "react-router-dom";


export default function UserProfile() {
    const location = useLocation()
    const id = new URLSearchParams(location.search).get("id");
    const user_r = location.state.user

    const [showModal, setShowModal] = useState(false);
    const [userToBlock, setUserToBlock] = useState(null);

    //const { userId } = useParams();
    //const [user, setUser] = useState(null);

    const user = {
      name:"Jesus",
      email:"jesus@gmail.com",
      phone:8003333,
      address:"Calle 1234",
      company: "Jesus SA"
    }

    function handleBlockUser(user) {
      setUserToBlock(user);
      setShowModal(true);
    }

    function handleConfirmBlockUser() {
        // hacer petición para bloquear usuario
        console.log(`Usuario ${userToBlock.name} bloqueado`);
        setShowModal(false);
    }

    function handleCancelBlockUser() {
        setUserToBlock(null);
        setShowModal(false);
    }  


    /*
    useEffect(() => {
      const fetchUser = async () => {
        const response = await fetch(`/api/users/${userId}`);
        const userData = await response.json();
        setUser(userData);
      };
      fetchUser();
    }, [userId]);

    if (!user) {
      return <div>Loading...</div>;
    }*/

    return (
      <div>
        <Sidebar></Sidebar>

        <Card>
          <Card.Body>
            <Card.Title>{user.name}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">
              {user.email}
            </Card.Subtitle>
            <ListGroup className="mb-3">
              <ListGroupItem>
                <b>Phone:</b> {user.phone}
              </ListGroupItem>
              <ListGroupItem>
                <b>Address:</b> {user.address}
              </ListGroupItem>
              <ListGroupItem>
                <b>Company:</b> {user.company}
              </ListGroupItem>
            </ListGroup>
            <div>
              <Button variant="primary">Edit Profile</Button>{" "}
              <Button variant="danger" onClick={() => handleBlockUser(user)}>
                     Block User
              </Button>
            </div>
          </Card.Body>
        </Card>

        <Modal show={showModal} onHide={handleCancelBlockUser}>
            <Modal.Header closeButton>
            <Modal.Title>Confirmación de Bloqueo de Usuario</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            ¿Estás seguro que quieres bloquear al usuario {userToBlock && userToBlock.name}?
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleCancelBlockUser}>
                Cancelar
            </Button>
            <Button variant="danger" onClick={handleConfirmBlockUser}>
                Bloquear
            </Button>
            </Modal.Footer>
        </Modal>

        <Link to="/users">Back to Users</Link>
      </div>
    );


}
